INSERT INTO `tb_movie` VALUES (0, '红高粱', 15.00, '张艺谋', '巩俐 / 姜文 / 滕汝骏', 'http://img.hb.aicdn.com/3436c1b331e6da359d8cb3a88a08d263ade80d4d8bd9d-9h15Pn', '该片改编自莫言1986年发表的两部中篇小说《红高粱》和《高粱酒》。因为一匹骡子，“ 我奶奶”九儿（巩俐饰）的父母要她嫁给一位拥有一座酒坊的50岁麻风病人。九儿乘坐花轿出嫁时，在途中被一名强盗拦截。', '1988-10-10', 1, '西安电影制片厂', 1, 8.5);
INSERT INTO `tb_movie` VALUES (1, '坏蛋联盟', 20.00, '彼埃尔·佩里菲尔', '山姆·洛克威尔，马克·马龙，克雷格·罗宾', 'http://182.92.102.155:8080/movie/17.jpg', '影片改编自艾伦·布雷比的同名漫画，讲述了全世界头号通缉团伙“坏蛋联盟”决定成为模范公民的故事。', '2022-04-29', 3, '梦工厂动画公司', 0, 9.0);
INSERT INTO `tb_movie` VALUES (2, '边缘行者', 34.50, '黄明升', '任贤齐、任达华、方中信、……', 'http://182.92.102.155:8080/movie/18.jpg', '该片讲述了从香港回归前夕特殊时代背景切入，通过卧底警察调查黑帮贩毒，揭露惊天政治阴谋的故事。', '2022-04-15', 4, '爱奇艺影业', 1, 5.3);
INSERT INTO `tb_movie` VALUES (3, '我要我们在一起', 36.90, '沙漠', '屈楚萧、张婧仪、……', 'http://img.hb.aicdn.com/8c60757b168a503d75624d008bb1174e9d993e96397b7-5oK0Rq', '该片改编自网络长帖《与我十年长跑的女友明天要嫁人了》，讲述了吕钦扬和凌一尧的十年爱情故事。 ', '2021-05-20', 2, '工夫影业（宁波）有限公司', 1, 6.0);
INSERT INTO `tb_movie` VALUES (4, '五个扑水的少年', 29.50, '宋灏霖', '辛云来、冯祥琨、李孝谦、……', 'http://182.92.102.155:8080/movie/16.jpg', '该片翻拍自日本电影《五个扑水的少年》 ，讲述了五名性格迥异的高中男生阴差阳错加入了学校的男子花样游泳队，为了心中重燃的热血，少年们决定一起迎接这场“男子花样游泳”的青春反击战的故事。', '2021-08-13', 3, '北京光线影业有限公司', 1, 7.2);
INSERT INTO `tb_movie` VALUES (5, '我是真的讨厌异地恋', 33.00, '吴洋、周男燊', '任敏、辛云来、……', 'http://img.hb.aicdn.com/da0c4ab8b5735d656de35a319ce53ef4e61401899ae8-jNTnE1', '该片围绕赵一一和许嘉树展开，讲述了赵一一终于向暗恋多年的许嘉树表白，却阴差阳错开启一场异地恋的故事。', '2022-04-29', 2, '北京光线影业有限公司', 1, 4.6);
INSERT INTO `tb_movie` VALUES (6, '神奇动物邓布利多之谜', 31.50, '大卫·耶茨', '埃迪·雷德梅恩、裘德·洛、……', 'http://182.92.102.155:8080/movie/15.jpg', '该片讲述邓布利多知道格林德沃想要统治魔法世界，他招募了纽特·斯卡曼德带领一个团队去执行一项危险的任务，他们将因此遇上新老神奇动物。', '2022-04-08', 1, '华纳兄弟影片公司', 1, 5.9);
INSERT INTO `tb_movie` VALUES (7, '神秘海域', 29.90, '鲁本·弗雷斯彻', '汤姆·赫兰德、……', 'http://182.92.102.155:8080/movie/19.jpg', '该片根据索尼的同名游戏改编，聚焦的是游戏前传，讲述年轻的内森如何与维克托·苏利文相识，并向他讨教“真经”的故事。', '2022-03-14', 4, '美国索尼哥伦比亚影片公司', 1, 6.3);
INSERT INTO `tb_movie` VALUES (8, '月球陨落', 30.50, '罗兰·艾默里奇', '帕特里克·威尔森、哈莉·贝瑞、乔什·盖德', 'http://182.92.102.155:8080/movie/20.jpg', '该片讲述了月球突然偏离运行轨道，即将撞上地球，而人类为了避免浩劫，决定派遣一支小队前往太空，试图探究月球失控的原因故事。', '2022-03-25', 3, '暂无', 1, 5.4);
INSERT INTO `tb_movie` VALUES (9, '大话西游之大圣娶亲', 25.98, '刘镇伟', '周星驰、吴孟达、朱茵', 'http://182.92.102.155:8080/movie/0.jpg', '第一部主要讲述了，唐僧师徒前去西天取经之前的孙悟空，因为要杀唐僧，被观音菩萨惩罚转世为至尊宝，而后遇见白骨精、蜘蛛精的传奇故事。', '2014-10-24', 1, '彩星电影公司', 1, 9.0);
INSERT INTO `tb_movie` VALUES (10, '无间道1', 30.00, '刘伟强、麦兆辉', '刘德华、梁朝伟', 'http://img.hb.aicdn.com/3300506f8c537631ca9e934db40e86ee71eb48ca1550e-8x53qA', '无间地狱乃佛经八大地狱中最苦一个，就如佛曰：“受身无间永远不死，寿并不是长乃无间地狱中之大劫。”，所有进入无间地狱的人永生又要经受时无间、空无间、器罪无间之苦，永无摆脱的希望。影片中梁朝伟扮演的陈永仁', '2005-06-16', 1, '寰亚电影发行公司', 1, 9.2);
INSERT INTO `tb_movie` VALUES (11, '霸王别姬', 30.00, '陈凯歌', ' 张国荣 、张丰', 'http://img.hb.aicdn.com/3ba7d11f104549efa4b0c4454261057517d5b506b72c-3ojws2', '段小楼（张丰毅）与程蝶衣（张国荣）是一对打小一起长大的师兄弟，两人一个演生，一个饰旦，一向配合天衣无缝，尤其一出《霸王别姬》，更是誉满京城，为此，两人约定合演一辈子《霸王别姬》。但两人对戏剧与人生关系', '1993-07-26', 1, '汤臣电影有限公司', 1, 9.6);
INSERT INTO `tb_movie` VALUES (12, '功夫', 30.00, ' 周星驰', ' 周星驰 / 元秋 / 元华 / 黄圣依', 'http://www.sfs-cn.com/images/thumbnailimg/month_1501/201501120217421060.jpg', '　1940年代的上海，自小受尽欺辱的街头混混阿星（周星驰）为了能出人头地，可谓窥见机会的缝隙就往里钻，今次他盯上行动日益猖獗的黑道势力“斧头帮”，想借之大名成就大业。', '2004-12-23', 1, '暂无', 1, 8.7);
INSERT INTO `tb_movie` VALUES (13, '我不是药神', 30.00, ' 文牧野', '徐峥 / 王传君 / 周一围', 'http://182.92.102.155:8080/movie/1.jpg', '普通中年男子程勇（徐峥 饰）经营着一家保健品店，失意又失婚。不速之客吕受益（王传君 饰）的到来，让他开辟了一条去印度买药做“代购”的新事业，虽然困难重重，但他在这条“买药之路”上发现了商机，一发不可收', '2018-07-05', 1, '暂无', 1, 9.0);
INSERT INTO `tb_movie` VALUES (14, '九品芝麻官', 30.00, ' 王晶', ' 周星驰 / 吴孟达 / 张敏', 'http://182.92.102.155:8080/movie/2.jpeg', '包龙星（周星驰 饰）用钱买来了一个候补知县的职位，可是为人贪婪，县上的人都对他痛恨之极。在他代替正式知县的时候，戚家媳妇戚秦氏（张敏 饰）遭到了常威( 邹兆龙 饰 )的强暴，后来常威动了杀机把戚家上下', '1994-03-31', 1, '暂无', 1, 8.7);
INSERT INTO `tb_movie` VALUES (15, '鬼子来了', 30.00, '姜文', ' 姜文 / 香川照之 / 袁丁 ', 'http://photocdn.sohu.com/20150205/mp721729_1423125836625_3.jpeg', '第二次世界大战期间，中国河北挂甲台村的农民马大三（姜文饰）在日本人的统治下过着平静的生活。一个晚上，游击队绑架了日军陆军士兵花屋小三郎（香川照之饰）和翻译董汉臣，把他们捆绑塞入麻袋放在马大三家里，并吩', '2000-05-12', 1, '华亿影视娱乐有限公司', 1, 9.3);
INSERT INTO `tb_movie` VALUES (16, '活着', 30.00, '张艺谋', ' 葛优 / 巩俐 / 姜武 ', 'http://img.defanli.com/bao/uploaded/i1/4154944887/O1CN01t9EOfu1lyI8t7qlEn_!!0-item_pic.jpg_q90.jpg', '根据余华同名小说改编。\r\n　　富少福贵（葛优）嗜赌成性，妻子家珍（巩俐）屡劝无果后带着女儿凤霞离开了他，当夜，福贵输光所有家产气死父亲，被迫靠变卖母亲首饰租间破屋过活。一年后，家珍手拉凤霞怀抱刚出世的', '1994-12-30', 1, '年代国际有限公司', 1, 9.3);
INSERT INTO `tb_movie` VALUES (17, '天书奇谭', 30.00, '王树忱 / 钱运达', ' 丁建华 / 毕克 / 苏秀 ', 'http://182.92.102.155:8080/movie/4.jpg', '　天庭有珍奇书籍“天书”，被锁在石龛门里三千年。看守天书的袁公对天书的内容好奇不已，他乘无人之际，偷偷拿出观看。原来，天书上记录了一百〇八条法术，袁公偷偷下凡，把天书上的内容刻在石壁上。但本意造福人类', '2021-11-05', 1, '中国上海美术电影制片厂', 0, 9.2);
INSERT INTO `tb_movie` VALUES (18, '大闹天宫', 30.00, ' 万籁鸣', '邱岳峰 / 富润生 / 毕克', 'http://hbimg.b0.upaiyun.com/96683b9d4cccfea18da69d9b304a98bbd8c3ff5e58091-eLfFcJ_fw658', '话说在东土傲来国有一座花果山，山上有一尊石猴吸收日精月华化身为一只神猴（邱岳峰 音），统领着山中的猴子猴孙。为求得一件称心的宝贝，神猴孙大圣潜入龙宫，强硬求来大禹治水时的定海神针如意金箍棒。东海龙王（', '1978-03-22', 1, '暂无', 1, 9.4);
INSERT INTO `tb_movie` VALUES (19, '茶馆', 30.00, ' 谢添', ' 于是之 / 郑榕 / 蓝天野', 'http://182.92.102.155:8080/movie/5.jpg', '世纪之交，古老的中国正迎来前所未有的巨大变革。老态龙钟的大清王朝摇摇欲坠，六君子的鲜血无法阻止历史车轮的滚动，老北京城的上空风云变幻，波谲云诡。王利发（于是之 饰），北京城内裕泰茶馆的年轻掌柜。他谨记', '1982-07-08', 1, '北京电影制片厂', 1, 9.5);
INSERT INTO `tb_movie` VALUES (20, '宝莲灯', 30.00, '常光希', ' 姜文 / 徐帆 / 宁静 / 陈佩斯', 'http://182.92.102.155:8080/movie/3.jpg', '天神爱上凡人，这原本就是不被允许的事情。但是，天宫中的三圣母（徐帆 配音）却执意爱上了人间的书生刘彦昌。不顾二郎神（姜文 配音）的反对，三圣母带着神器宝莲灯私下凡间与刘彦昌私定终身并生下一子取名叫沉香', '1999-07-30', 1, '暂无', 1, 8.4);
INSERT INTO `tb_movie` VALUES (21, '爱情神话', 28.80, ' 邵艺辉', '徐峥 / 马伊琍 / 吴越 ', 'http://img.hb.aicdn.com/96a2748a61fd5e8021423f4231e419728594b9c112856-3hboib', '离异多年的画家老白（徐峥 饰）经朋友介绍认识了同样离异，带着一个孩子的李小姐（马伊琍 饰），老白对李小姐一见倾心，可是李小姐似乎对这段感情有所顾虑，一直止步不前。老白在家里开设了美术班，教成年人画画，', '2021-12-24', 2, '麦特影业（湖北）有限公司', 1, 8.1);
INSERT INTO `tb_movie` VALUES (22, '花束般的恋爱', 32.20, ' 土井裕泰', '菅田将晖 / 有村架纯 / 细田佳央太', 'http://img5.mtime.cn/mg/2021/01/25/145408.96472125.jpg', '山音麦 (菅田将晖 饰) 和八谷绢 (有村架纯 饰) 因错过尾班车而相遇，在深夜咖啡馆聊起文学、电影和音乐，喜好竟奇蹟地相似，二人瞬间坠入爱河。他们毕业后开始一边兼职工作，一边开始同居生活，更一起养拾', '2022-02-22', 2, '日本东京广播电视台闪股份有限公司', 1, 8.6);
INSERT INTO `tb_movie` VALUES (23, '我在时间尽头等你', 28.80, ' 姚婷婷', ' 李鸿其 / 李一桐 / 范伟', 'http://qqpublic.qpic.cn/qq_public/0/0-2440467050-D3D7719F32F99E30AC2A058060FD043C/0?fmt=jpg&size=65&', '林格（李鸿其 饰）人生所有美好的记忆，都与邱倩（李一桐 饰）有关。一场意外、一块神奇的表彻底改变了两人的人生......时空不断被重置，唯一不变的，是爱你如初的心。', '2020-08-25', 2, '阿里影业', 1, 5.3);
INSERT INTO `tb_movie` VALUES (24, '大话西游之月光宝盒', 33.39, ' 刘镇伟', ' 周星驰 / 吴孟达 / 罗家英', 'http://182.92.102.155:8080/movie/6.jpg', '孙悟空（周星驰）护送唐三藏（罗家英）去西天取经路上，与牛魔王合谋欲杀害唐三藏，并偷走了月光宝盒，此举使观音萌生将其铲除心思，经唐三藏请求，孙悟空被判五百年后重新投胎做人赎其罪孽。\r\n　　五百年后孙悟空', '2014-10-24', 2, '彩星电影公司', 0, 9.0);
INSERT INTO `tb_movie` VALUES (25, '你好，李焕英', 28.40, ' 贾玲', ' 贾玲 / 孙集斌 / 王宇 ', 'http://182.92.102.155:8080/movie/8.jpg', '　2001年的某一天，刚刚考上大学的贾晓玲（贾玲 饰）经历了人生中的一次大起大落。一心想要成为母亲骄傲的她却因母亲突遭严重意外，而悲痛万分。在贾晓玲情绪崩溃的状态下，竟意外的回到了1981年，并与年轻', '2021-02-12', 3, '暂无', 1, 7.7);
INSERT INTO `tb_movie` VALUES (26, '唐人街探案3', 32.80, ' 陈思诚', '王宝强 / 刘昊然 / 妻夫木聪', 'http://182.92.102.155:8080/movie/7.jpg', '　继曼谷、纽约之后，东京再出大案。唐人街神探唐仁（王宝强 饰）、秦风（刘昊然 饰）受侦探野田昊（妻夫木聪 饰）的邀请前往破案。“CRIMASTER世界侦探排行榜”中的侦探们闻讯后也齐聚东京，加入挑战，', '2021-02-12', 3, '万达影视传媒有限公司', 1, 5.3);
INSERT INTO `tb_movie` VALUES (27, '一点就到家', 24.80, ' 许宏宇', ' 刘昊然 / 彭昱畅 / 尹昉', 'http://182.92.102.155:8080/movie/21.jpg', '魏晋北（刘昊然 饰）、彭秀兵（彭昱畅 饰）、李绍群（尹昉 饰）三个性格迥异的年轻人从大城市回到云南千年古寨，机缘巧合下共同创业，与古寨“格格不入”的他们用真诚改变了所有人，开启了一段非常疯狂、纯真的梦', '2020-10-04', 3, '北京嘉映春天影业有限公司', 1, 6.5);
INSERT INTO `tb_movie` VALUES (28, '扬名立万', 32.80, ' 刘循子墨', ' 尹正 / 邓家佳 / 喻恩泰', 'http://182.92.102.155:8080/movie/9.jpg', '月黑风高之夜，一群电影人被秘密召集到一起，欲将轰动一时的血案翻拍成电影，借此扬名立万。殊不知他们正身处案发现场，并步步陷入一个巨大迷局之中，而凶手就在他们中间……', '2021-11-11', 3, '上海猫眼影业有限公司', 1, 7.4);
INSERT INTO `tb_movie` VALUES (29, '四海', 30.00, '韩寒', ' 刘昊然 / 刘浩存 / 沈腾 ', 'http://n.sinaimg.cn/ent/transform/560/w550h810/20210821/16d5-3604e74be7bb0f2fae3a789cbd61b5fa.jpg', '　在码头做摩托车特技表演顺便拉客的年轻人吴仁耀（刘昊然 饰），他多年不见的浪荡父亲吴仁腾（沈腾 饰）；梦想大城市生活的餐馆服务员周欢颂和他浮夸真诚的哥哥周欢歌。一支从没有赢过一场比赛的“不败传说”车队', '2022-02-01', 3, '暂无', 1, 5.4);
INSERT INTO `tb_movie` VALUES (30, '疯狂动物城 Zootopia', 38.80, ' 拜伦·霍华德 / 瑞', '金妮弗·古德温 / 杰森·贝特曼', 'http://182.92.102.155:8080/movie/10.jpg', '　故事发生在一个所有哺乳类动物和谐共存的美好世界中，兔子朱迪（金妮弗·古德温 Ginnifer Goodwin 配音）从小就梦想着能够成为一名惩恶扬善的刑警，凭借着智慧和努力，朱迪成功的从警校中毕业进', '2016-03-04', 3, '迪士尼影业出品', 1, 9.2);
INSERT INTO `tb_movie` VALUES (31, '菊次郎的夏天', 30.00, ' 北野武', ' 北野武 / 关口雄介 / 岸本加世子', 'http://p0.itc.cn/q_70/images03/20200928/3d6e9a681e0749ba8c5cf602f03507c3.png', '　暑假到来，自幼丧父的小学三年级学生正男（关口雄介饰），如今和奶奶生活在一起，暑假特别无聊。他拿出母亲从外地寄回的信，准备自己一个人去爱知县丰桥市看望母亲。', '2020-09-25', 3, '安乐影片有限公司', 1, 8.9);
INSERT INTO `tb_movie` VALUES (32, '千与千寻', 28.80, ' 宫崎骏', ' 柊瑠美 / 入野自由 / 夏木真', 'http://182.92.102.155:8080/movie/11.jpg', '　千寻和爸爸妈妈一同驱车前往新家，在郊外的小路上不慎进入了神秘的隧道——他们去到了另外一个诡异世界—一个中世纪的小镇。远处飘来食物的香味，爸爸妈妈大快朵颐，孰料之后变成了猪！这时小镇上渐渐来了许多样子', '2019-06-21', 4, '东宝映画', 1, 9.4);
INSERT INTO `tb_movie` VALUES (33, '大鱼海棠', 32.00, ' 梁旋 / 张春', ' 季冠霖 / 苏尚卿 / 许魏洲', 'http://182.92.102.155:8080/movie/12.jpg', '所有活着的人类，都是海里一条巨大的鱼；出生的时候他们从海的此岸出发。他们的生命就像横越大海，有时相遇，有时分开……死的时候，他们便到了岸，各去各的世界。', '2016-07-08', 4, '彼岸天文化有限公司', 1, 6.9);
INSERT INTO `tb_movie` VALUES (34, '白蛇：缘起', 28.80, ' 黄家康 / 赵霁', ' 张喆 / 杨天翔 / 唐小喜', 'http://img.hb.aicdn.com/3f69a7b63939bea5357297e07bac84e7feb27800610b1f-BKSStZ', '幽暗洞中，白蛇（张喆 配音）苦苦修炼却不得其法，小青见此情景，将发髻上的碧玉簪子取下，令白蛇攥在手中。那一刻，五百年前的记忆瞬间苏醒。五百年前，晚唐君主昏聩庸碌，掌握邪术的国师只手遮天，命令天下百姓捕', '2019-01-11', 4, '追光动画', 1, 7.8);
INSERT INTO `tb_movie` VALUES (35, '哪吒之魔童降世', 30.00, ' 饺子', '吕艳婷 / 囧森瑟夫 / 瀚墨 ', 'http://182.92.102.155:8080/movie/13.jpg', '天地灵气孕育出一颗能量巨大的混元珠，元始天尊将混元珠提炼成灵珠和魔丸，灵珠投胎为人，助周伐纣时可堪大用；而魔丸则会诞出魔王，为祸人间。元始天尊启动了天劫咒语，3年后天雷将会降临，摧毁魔丸。太乙受命将灵', '2019-07-26', 4, '霍尔果斯彩条屋影业有限公司', 1, 8.4);
INSERT INTO `tb_movie` VALUES (36, '大圣归来', 35.50, ' 田晓鹏', ' 张磊 / 林子杰 / 吴文伦 ', 'http://img.hb.aicdn.com/a6f5fecba0db20f24bdda74804ff60eba9c74ba315fdbe-d29Pkc', '五百年前，由石猴变化而成的齐天大圣孙悟空（张磊 配音）大闹天宫，最终被如来佛祖镇在了五行山下。此去经年，长安城内突然遭到山妖洗劫，童男童女哭声连连，命悬一线。危机时刻，自幼被行脚僧法明（吴文伦 配音）', '2015-07-10', 4, '高路动画', 1, 8.3);
INSERT INTO `tb_movie` VALUES (37, '罗小黑战记', 35.50, ' 木头', '山新 / 郝祥海 / 刘明月', 'https://img2.doubanio.com/view/photo/l/public/p2566171062.webp', '在熙攘的人类世界里，很多妖精隐匿其中，他们与人类相安无事地生活着。猫妖罗小黑因为家园被破坏，开始了它的流浪之旅。这场旅途中惺惺相惜的妖精同类与和谐包容的人类伙伴相继出现，让小黑陷入了两难抉择，究竟何处', '2019-09-07', 4, '北京基因映画影业有限公司', 1, 8.0);
